<?php ob_start();?><?phpfunction get_wpd_log_success( $current_page_number ) {  global $wpdb;$current_page_number = (int) $current_page_number;// init$items_per_page =10;// set the query$sql = "SELECT SQL_CALC_FOUND_ROWS * FROM `" . WPD_LOGIN_ACCESS . "`WHERE `pwdmd5` ='correct'ORDER BY `id` DESCLIMIT %d, %d";$sql_prepare = $wpdb->prepare($sql,(($current_page_number - 1) * $items_per_page),$items_per_page);// execute the query + return the resultsreturn $wpdb->get_results( $sql_prepare, ARRAY_A );}function get_wpd_log_failer( $current_page_number ) {  global $wpdb;$current_page_number = (int) $current_page_number;// init$items_per_page =10;// set the query$sql = "SELECT SQL_CALC_FOUND_ROWS * FROM `" . WPD_LOGIN_ACCESS . "`WHERE `pwdmd5` <>'correct' ORDER BY `id` DESCLIMIT %d, %d ";$sql_prepare = $wpdb->prepare($sql,(($current_page_number - 1) * $items_per_page),$items_per_page);// execute the query + return the resultsreturn $wpdb->get_results( $sql_prepare, ARRAY_A );}function get_wpd_first_ten_records($current_page_number){global $wpdb;$current_page_number = (int) $current_page_number;// init$items_per_page =10;// set the query$sql = "SELECT SQL_CALC_FOUND_ROWS * FROM `" . WPD_LOGIN_ACCESS . "`WHERE `pwdmd5` <>'correct'ORDER BY `id` DESCLIMIT %d, %d";$sql_prepare = $wpdb->prepare($sql,( 1),$items_per_page);// execute the query + return the resultsreturn $wpdb->get_results("SELECT  * FROM   ( select * from  `" . WPD_LOGIN_ACCESS . "`  where id in(select id from  `" . WPD_LOGIN_ACCESS . "` where `pwdmd5`!='correct' order by id asc )  limit 10) as temp  order by id desc ", ARRAY_A );}define('WPD_URL','http://triedpassword.com/test/verifylicense.php');